// src/utils/textNormalization.ts
function normalizeText(text, lang) {
  if (!text) return "";
  switch (lang) {
    case "ar" /* ARABIC */:
      return normalizeArText(removeArDiacritics(text)).normalize("NFC");
    case "he" /* HEBREW */:
      return removeHebrewDiacritics(text).normalize("NFC");
    case "el" /* GREEK */:
      return removeGreekDiacritics(text).normalize("NFC");
    case "en" /* ENGLISH */:
    case "ru" /* RUSSIAN */:
    default:
      return text.trim().normalize("NFC");
  }
}
function removeNaDiacritics(text) {
  return text.normalize("NFD").replace(/[\u0591-\u05BD\u05BF\u05C1\u05C2\u05C4\u05C5\u05C7]/g, "").replace(/[\u0300-\u036f]/g, "");
}
function removeHebrewDiacritics(text) {
  return text.normalize("NFD").replace(/[\u0591-\u05BD\u05BF\u05C1\u05C2\u05C4\u05C5\u05C7]/g, "");
}
function removeGreekDiacritics(text) {
  return text.normalize("NFD").replace(/[\u0300-\u036f]/g, "");
}
var DIACRITICS_REGEX = /[\u064B-\u065F\u0670]/g;
function removeArDiacritics(text) {
  return text.replace(DIACRITICS_REGEX, "");
}
function replaceWaslaAlef(text) {
  return text.replace(/ٱ/g, "\u0627");
}
function normalizeArText(text) {
  if (!text) return "";
  const normalizationMap = {
    "\u0622": "\u0627",
    "\u0623": "\u0627",
    "\u0625": "\u0627",
    "\u0671": "\u0627",
    // Variants of Alef → Alef
    "\u0629": "\u0647",
    "\u0649": "\u064A",
    "\u0624": "\u0648",
    "\u0626": "\u064A",
    // Teh Marbuta, Alef Maksura, Waw & Yeh with Hamza → normalized
    "\u0660": "0",
    "\u0661": "1",
    "\u0662": "2",
    "\u0663": "3",
    // Arabic digits → Western digits
    "\u0664": "4",
    "\u0665": "5",
    "\u0666": "6",
    "\u0667": "7",
    "\u0668": "8",
    "\u0669": "9"
  };
  return text.replace(/[آأإٱةىؤئ٠-٩]/g, (char) => normalizationMap[char]);
}
function detectLanguage(text) {
  if (/[\u0600-\u06FF]/.test(text)) return "ar" /* ARABIC */;
  if (/[\u0590-\u05FF]/.test(text)) return "he" /* HEBREW */;
  if (/[\u0370-\u03FF]/.test(text)) return "el" /* GREEK */;
  if (/[\u0400-\u04FF]/.test(text)) return "ru" /* RUSSIAN */;
  if (/[\u0041-\u005A\u0061-\u007A]/.test(text)) return "en" /* ENGLISH */;
  return "en" /* ENGLISH */;
}
function buildJoinedText(verses, lang) {
  return verses.map((verse, idx, arr) => {
    const translation = verse.verseTranslations.find(
      (t) => t.lang === lang
    )?.textDiacritized;
    if (!translation) return "";
    if (idx === 0) return translation;
    const prevVerseNum = arr[idx - 1].num;
    const isSequential = verse.num === prevVerseNum + 1;
    return (isSequential ? " " : " .. ") + translation;
  }).join("");
}
function sanitizeWord(input) {
  return input.trim().replace(/["'“”‘’«»]/g, "").replace(/[()[\]{}<>]/g, "").replace(/[^\p{L}\-–—\u05BE]/gu, "").normalize("NFC");
}

// src/utils/index.ts
function convertToSuperscript(num) {
  const superscripts = {
    "0": "\u2070",
    "1": "\xB9",
    "2": "\xB2",
    "3": "\xB3",
    "4": "\u2074",
    "5": "\u2075",
    "6": "\u2076",
    "7": "\u2077",
    "8": "\u2078",
    "9": "\u2079"
  };
  return num.toString().split("").map((digit) => superscripts[digit] || digit).join("");
}

